        public static void UseItem(Game.MsgServer.MsgGameItem item, Client.GameClient client, ServerSockets.Packet stream)
        {
            if (!client.Player.Alive)
                return;
            Database.ItemType.DBItem DBItem;
            if (Database.Server.ItemsBase.TryGetValue(item.ITEM_ID, out DBItem))
            {
                if (client.PipeServer != null)
                {
                    if (item.ITEM_ID >= 1000000 && item.ITEM_ID <= 1000040
                  || item.ITEM_ID >= 1002000 && item.ITEM_ID <= 1002030
                  || item.ITEM_ID == 1002050
                  || item.ITEM_ID == 725065 || item.ITEM_ID == 1003010)
                    {
                        if (DateTime.Now > client.Player.MedicineStamp.AddMilliseconds(150))
                        {
                            if (client.EventBase != null && client.EventBase.PotionsAllowed == false && client.EventBase.Stage == MsgEvents.EventStage.Fighting)
                                return;
                            if (client.Player.ContainFlag(MsgUpdate.Flags.PoisonStar) && item.ITEM_ID != 1003010)
                                return;
                            if (client.Player.HitPoints == client.Status.MaxHitpoints)
                                return;
                            if (Program.NoHP.Contains(client.Player.Map) || (Game.AISystem.UnlimitedArenaRooms.Maps.ContainsValue(client.Player.DynamicID) && client.Player.fbss == 1))
                            {
                                client.SendSysMesage("No HP In This Map DIABLO.");
                                return;
                            }
